const getCurrentDateString = () => {
  const today = new Date();
  const year = today.getFullYear();
  const month = String(today.getMonth() + 1).padStart(2, '0');
  const day = String(today.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

const getResistanceLine = (daysSinceGb) => {
  return (Math.pow(10, -13.14005841) * Math.pow(daysSinceGb, 4.218461)).toFixed(3);
};

const getFairPrice = (daysSinceGb) => {
  return (Math.pow(10, -13.25978043) * Math.pow(daysSinceGb, 4.218461)).toFixed(3);
};

const getSupportPrice = (daysSinceGb) => {
  return (Math.pow(10, -13.37950245) * Math.pow(daysSinceGb, 4.218461)).toFixed(3);
};

const calculatePercentDelta = (currentPrice, fairPrice) => {
  return (100 * ((currentPrice - fairPrice) / fairPrice)).toFixed(1);
};

function calculateDaysSinceGb(currentDate) {
  // Parse the input date string
  const inputDate = new Date(currentDate);

  // Define the reference date
  const referenceDate = new Date('2021-11-07');

  // Calculate the difference in time (milliseconds)
  const timeDifference = inputDate - referenceDate;

  // Convert the difference from milliseconds to days
  const daysDifference = timeDifference / (1000 * 60 * 60 * 24);

  return Math.round(daysDifference);
}

async function getKasCurrentPrice() {
  try {
    console.log(GET_KAS_PRICE);
    const response = await fetch(GET_KAS_PRICE, { method: 'GET' });
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    const response_json = await response.json();
    return parseFloat(response_json.price).toFixed(3);
  } catch (error) {
    console.error('Error fetching crypto price:', error);
    throw error;
  }
}

