import React, { useState, useEffect } from 'react';
import { Container, Typography, Paper, Box, TextField } from '@mui/material';
import { GET_KAS_PRICE } from './constants/urls';

// Function to get the current date as a string in YYYY-MM-DD format
const getCurrentDateString = () => {
  const today = new Date();
  const year = today.getFullYear();
  const month = String(today.getMonth() + 1).padStart(2, '0');
  const day = String(today.getDate()).padStart(2, '0');
  return `${year}-${month}-${day}`;
};

const getResistanceLine = (daysSinceGb) => {
  return (Math.pow(10, -13.39783736) * Math.pow(daysSinceGb, 4.225098)).toFixed(3);
};

const getFairPrice = (daysSinceGb) => {
  return (Math.pow(10, -13.15628951) * Math.pow(daysSinceGb, 4.225098)).toFixed(3);
}

const getSupportPrice = (daysSinceGb) => {
  return (Math.pow(10, -13.27706344) * Math.pow(daysSinceGb, 4.225098)).toFixed(3);
};


function calculateDaysSinceGb(currentDate) {
  // Parse the input date string
  const inputDate = new Date(currentDate);

  // Define the reference date
  const referenceDate = new Date('2021-11-07');

  // Calculate the difference in time (milliseconds)
  const timeDifference = inputDate - referenceDate;

  // Convert the difference from milliseconds to days
  const daysDifference = timeDifference / (1000 * 60 * 60 * 24);

  return Math.round(daysDifference);
}

async function getKasCurrentPrice() {
  try {
    console.log(GET_KAS_PRICE);
    const response = await fetch(GET_KAS_PRICE, { method: 'GET' });
    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }
    const response_json = await response.json();
    return parseFloat(response_json.price).toFixed(3);
  } catch (error) {
    console.error('Error fetching crypto price:', error);
    throw error;
  }
}

function App() {
  const currentDate = getCurrentDateString();
  const daysSinceGb = calculateDaysSinceGb(currentDate);

  const [selectedDate, setSelectedDate] = useState(currentDate);
  const [supportPrice, setSupportPrice] = useState(getSupportPrice(daysSinceGb));
  const [fairPrice, setFairPrice] = useState(getFairPrice(daysSinceGb));
  const [resistancePrice, setResistancePrice] = useState(getResistanceLine(daysSinceGb));
  const [currentPrice, setCurrentPrice] = useState(' - ');

  const handleDateChange = (event) => {
    const newDate = event.target.value;
    setSelectedDate(newDate);
    const daysSinceGb = calculateDaysSinceGb(newDate);
    setSupportPrice(getSupportPrice(daysSinceGb));
    setFairPrice(getFairPrice(daysSinceGb));
    setResistancePrice(getResistanceLine(daysSinceGb));
  };

  useEffect(() => {
    async function fetchData() {
      const kasPrice = await getKasCurrentPrice();
      setCurrentPrice(kasPrice);
    }
    fetchData();
  }, []);

  return (
    <Container
      maxWidth="sm"
      style={{
        backgroundColor: '#111111',
        padding: '20px',
        borderRadius: '10px',
        color: '#fff',
        marginTop: '20px',
      }}
    >
      <Typography
        variant="h4"
        component="h1"
        gutterBottom
        align="center"
        style={{ color: '#fff' }}
      >
        Kaspa Power Law
      </Typography>
      <Typography
        variant="h6"
        component="h2"
        gutterBottom
        align="center"
        style={{ color: '#ccc', marginBottom: '15px' }}
      >
        Price according to the power law
      </Typography>
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        marginBottom="20px"
      >
        <TextField
          id="date"
          label="Select a Date"
          type="date"
          value={selectedDate}
          onChange={handleDateChange}
          InputLabelProps={{
            shrink: true,
          }}
          sx={{
            '& .MuiInputBase-root': {
              color: '#fff',
            },
            '& .MuiInputLabel-root': {
              color: '#fff',
            },
            '& .MuiOutlinedInput-root': {
              '& fieldset': {
                borderColor: '#555',
              },
              '&:hover fieldset': {
                borderColor: '#777',
              },
              '&.Mui-focused fieldset': {
                borderColor: '#999',
              },
            },
          }}
        />
      </Box>
      <Paper
        elevation={3}
        style={{
          padding: '20px',
          backgroundColor: '#292828',
          textAlign: 'center',
        }}
      >
        <Typography
          variant="h6"
          component="p"
          style={{ color: 'white', fontWeight: 'bolder' }}
        >
          Resistance:
        </Typography>
        <Typography
          variant="h6"
          component="p"
          style={{ color: 'green', fontWeight: 'bolder' }}
        >
          ${resistancePrice}
        </Typography>
        <Typography
          variant="h6"
          component="p"
          style={{ color: 'white', fontWeight: 'bolder' }}
        >
          Fair Price:
        </Typography>
        <Typography
          variant="h6"
          component="p"
          style={{ color: '#6EC6BB', fontWeight: 'bolder' }}
        >
          ${fairPrice}
        </Typography>
        <Typography
          variant="h6"
          component="p"
          style={{ color: 'white', fontWeight: 'bolder' }}
        >
          Support Price:
        </Typography>
        <Typography
          variant="h6"
          component="p"
          style={{ color: '#A62300', fontWeight: 'bolder' }}
        >
          ${supportPrice}
        </Typography>
        <Typography
          variant="h6"
          component="p"
          style={{ color: 'white', fontWeight: 'bolder' }}
        >
          Current Price:
        </Typography>
        <Typography
          variant="h6"
          component="p"
          style={{ color: 'green', fontWeight: 'bolder' }}
        >
          ${currentPrice}
        </Typography>
      </Paper>
      <Typography
        variant="h6"
        component="h2"
        gutterBottom
        align="center"
        style={{ marginTop: '20px', color: '#ccc' }}
      >
        FAQ
      </Typography>
      <Paper
        elevation={3}
        style={{
          padding: '20px',
          backgroundColor: '#444',
        }}
      >
        <Typography variant="body1" component="p" style={{ color: '#fff' }}>
          <strong>1) What is Kaspa?</strong>
        </Typography>
        <Typography variant="body2" component="p" style={{ color: '#ccc' }}>
          Bitcoin's relationship with a power law was discovered by Giovanni Santostasi when he realized that bitcoin's price moved in a straight line when it was plotted in a log-log graph.
        </Typography>
        <Typography variant="body1" component="p" style={{ color: '#fff' }}>
          <strong>2) What is Kaspa's Power Law Model?</strong>
        </Typography>
        <Typography variant="body2" component="p" style={{ color: '#ccc' }}>
          Bitcoin's relationship with a power law was discovered by Giovanni Santostasi when he realized that bitcoin's price moved in a straight line when it was plotted in a log-log graph.
        </Typography>
        <Typography
          variant="body2"
          component="p"
          style={{ marginTop: '10px', color: '#ccc' }}
        >
          This is the only asset that behaves this way, indicating there is something intrinsic about the nature of Bitcoin which allows us to predict its price into the future.
        </Typography>
      </Paper>
    </Container>
  );
}

export default App;
