import React, { useRef, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LogarithmicScale,
  TimeScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';
import 'chartjs-adapter-date-fns';
import annotationPlugin from 'chartjs-plugin-annotation';
import zoomPlugin from 'chartjs-plugin-zoom';

// Register necessary components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LogarithmicScale,
  TimeScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
  annotationPlugin,
  zoomPlugin
);

const getResistancePrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.14005841) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};
const getFairPrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.25978043) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};
const getSupportPrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.37950245) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};

const PriceChart = ({ priceData }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    const chart = chartRef.current;
    if (chart) {
      chart.canvas.ondblclick = () => {
        chart.resetZoom();
      };
    }
  }, [chartRef]);

  const startDate = new Date('2021-11-07');
  const days = Array.from({ length: 3650 - 100 + 1 }, (_, i) => i + 100); // Example for 100 to 3650 days
  const dates = days.map(day => {
    const date = new Date(startDate);
    date.setDate(startDate.getDate() + day);
    return date;
  });
  const resistancePrices = days.map(getResistancePrice);
  const fairPrices = days.map(getFairPrice);
  const supportPrices = days.map(getSupportPrice);

  const annotations = dates
    .filter(date => date.getDate() === startDate.getDate() && date.getMonth() === startDate.getMonth()) // Filter for the same day and month each year
    .map(date => ({
      type: 'line',
      xMin: date.getTime(),
      xMax: date.getTime(),
      borderColor: 'rgba(255, 255, 255, 1)', // White color
      borderWidth: 1,
      label: {
        enabled: true,
        content: date.toISOString().split('T')[0],
        position: 'top',
        backgroundColor: 'rgba(255, 255, 255, 1)',
        color: 'black',
      }
    }));

  const priceDataPoints = Array.isArray(priceData) ? priceData.map(item => ({
    x: new Date(item.date).getTime(),
    y: item.price,
  })) : [];

  const data = {
    labels: dates.map(date => date.getTime()),
    datasets: [
      {
        label: 'Resistance Price',
        data: resistancePrices,
        borderColor: 'rgba(255, 99, 132, 1)', // Red color for Resistance Price
        backgroundColor: 'rgba(255, 99, 132, 1)',
        fill: false,
        pointRadius: 0,
        pointHitRadius: 10, // Increase hit radius for better hover detection
      },
      {
        label: 'Fair Price',
        data: fairPrices,
        borderColor: 'rgba(54, 162, 235, 1)', // Blue color for Fair Price
        backgroundColor: 'rgba(54, 162, 235, 1)',
        fill: false, // Disable fill
        pointRadius: 0,
        pointHitRadius: 10, // Increase hit radius for better hover detection
      },
      {
        label: 'Support Price',
        data: supportPrices,
        borderColor: 'rgba(75, 192, 192, 1)', // Teal color for Support Price
        backgroundColor: 'rgba(75, 192, 192, 1)',
        fill: false,
        pointRadius: 0,
        pointHitRadius: 10, // Increase hit radius for better hover detection
      },
      {
        label: 'Price Data',
        data: priceDataPoints,
        borderColor: 'rgba(255, 165, 0, 1)', // Orange color for Price Data
        backgroundColor: 'rgba(255, 165, 0, 1)',
        fill: false,
        pointRadius: 0, // Remove points
        pointHitRadius: 10, // Increase hit radius for better hover detection
      },
    ],
  };

  const options = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'year',
          tooltipFormat: 'yyyy-MM-dd',
          displayFormats: {
            year: 'yyyy-MM-dd',
          }
        },
        title: {
          display: true,
          text: 'Date',
        },
        ticks: {
          callback: function (value, index, values) {
            const date = new Date(value);
            return ' ' + date.toISOString().split('T')[0]; // Add space before date to move it left
          },
        },
      },
      y: {
        type: 'logarithmic',
        title: {
          display: true,
          text: 'Price',
        },
        ticks: {
          callback: function (value) {
            const logValue = Math.log10(value);
            if (Number.isInteger(logValue)) {
              return value;
            }
            return null;
          },
        },
      },
    },
    plugins: {
      annotation: {
        annotations: annotations
      },
      zoom: {
        zoom: {
          drag: {
            enabled: true,
            borderColor: 'rgba(54, 162, 235, 0.5)',
            borderWidth: 1,
            backgroundColor: 'rgba(54, 162, 235, 0.3)',
          },
          mode: 'xy',
        },
        pan: {
          enabled: false,
        },
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const index = context.dataIndex;
            const datasets = context.chart.data.datasets;
            const resistance = datasets[0].data[index];
            const fair = datasets[1].data[index];
            const support = datasets[2].data[index];
            const price = priceDataPoints[index] && priceDataPoints[index].y !== undefined ? priceDataPoints[index].y : 'N/A';
            return [
              `Resistance Price: ${resistance}`,
              `Fair Price: ${fair}`,
              `Support Price: ${support}`,
              `Price Data: ${price}`
            ];
          },
          labelColor: function(context) {
            const colors = {
              'Resistance Price': { borderColor: 'rgba(255, 99, 132, 1)', backgroundColor: 'rgba(255, 99, 132, 1)' },
              'Fair Price': { borderColor: 'rgba(54, 162, 235, 1)', backgroundColor: 'rgba(54, 162, 235, 1)' },
              'Support Price': { borderColor: 'rgba(75, 192, 192, 1)', backgroundColor: 'rgba(75, 192, 192, 1)' },
              'Price Data': { borderColor: 'rgba(255, 165, 0, 1)', backgroundColor: 'rgba(255, 165, 0, 1)' },
            };
            return colors[context.dataset.label];
          }
        },
        mode: 'nearest',
        intersect: true, // Only show tooltip when intersecting with an element
      },
    },
  };

  return (
    <div>
      <Line ref={chartRef} data={data} options={options} />
    </div>
  );
};

export default PriceChart;
