import React, { useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import qr_code from 'assets/donation.jpeg';

const DonationWidget = () => {
  const [copied, setCopied] = useState(false);

  const address = 'kaspa:qpk37e7pn9x9nm85nqnwyk8ffjp6tln8m8sna5vdd6rw068zy2xvu6jf4q7ep';

  const handleCopy = () => {
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', padding: '20px', border: '1px solid #ccc', borderRadius: '10px', width: '250px', margin: '0 auto' }}>
      <h3>Donate Crypto</h3>
      <img src={qr_code} alt="Kaspa QR Code" style={{ width: '128px', height: '128px' }}/>
      <p style={{ wordBreak: 'break-all', textAlign: 'center', margin: '10px 0' }}>{address}</p>
      <CopyToClipboard text={address} onCopy={handleCopy}>
        <button style={{ padding: '10px 20px', cursor: 'pointer', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '5px' }}>Copy Address</button>
      </CopyToClipboard>
      {copied && <span style={{ marginTop: '10px', color: 'green' }}>Address copied!</span>}
    </div>
  );
};

export default DonationWidget;
