import React, { useRef, useEffect } from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LogarithmicScale,
  TimeScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';
import 'chartjs-adapter-date-fns';
import annotationPlugin from 'chartjs-plugin-annotation';
import zoomPlugin from 'chartjs-plugin-zoom';

// Register necessary components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LogarithmicScale,
  TimeScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
  annotationPlugin,
  zoomPlugin
);

const getResistancePrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.14005841) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};
const getFairPrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.25978043) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};
const getSupportPrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.37950245) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};

const PriceChart = () => {
  const chartRef = useRef(null);

  useEffect(() => {
    const chart = chartRef.current;

    if (chart) {
      chart.canvas.ondblclick = () => {
        chart.resetZoom();
      };

      chart.canvas.onclick = (event) => {
        const points = chart.getElementsAtEventForMode(event, 'nearest', { intersect: false }, false);
        if (points.length) {
          const firstPoint = points[0];
          const label = chart.data.labels[firstPoint.index];
          const value1 = chart.data.datasets[0].data[firstPoint.index];
          const value2 = chart.data.datasets[1].data[firstPoint.index];
          const value3 = chart.data.datasets[2].data[firstPoint.index];

          chart.tooltip.setActiveElements([
            { datasetIndex: 0, index: firstPoint.index },
            { datasetIndex: 1, index: firstPoint.index },
            { datasetIndex: 2, index: firstPoint.index }
          ], { x: event.x, y: event.y });

          chart.tooltip.update();
          chart.draw();
        }
      };
    }
  }, []);

  const startDate = new Date('2021-11-07');
  const days = Array.from({ length: 3650 - 100 + 1 }, (_, i) => i + 100); // Example for 100 to 3650 days
  const dates = days.map(day => {
    const date = new Date(startDate);
    date.setDate(startDate.getDate() + day);
    return date;
  });
  const resistancePrices = days.map(getResistancePrice);
  const fairPrices = days.map(getFairPrice);
  const supportPrices = days.map(getSupportPrice);

  const annotations = dates
    .filter(date => date.getDate() === startDate.getDate() && date.getMonth() === startDate.getMonth()) // Filter for the same day and month each year
    .map(date => ({
      type: 'line',
      xMin: date.getTime(),
      xMax: date.getTime(),
      borderColor: 'rgba(255, 255, 255, 1)', // White color
      borderWidth: 1,
      label: {
        enabled: true,
        content: date.toISOString().split('T')[0],
        position: 'top',
        backgroundColor: 'rgba(255, 255, 255, 1)',
        color: 'black',
      }
    }));

  const data = {
    labels: dates.map(date => date.getTime()),
    datasets: [
      {
        label: 'Resistance Price',
        data: resistancePrices,
        borderColor: 'rgba(255, 99, 132, 1)',
        fill: false,
        pointRadius: 0,
        pointHitRadius: 10, // Increase hit radius for better hover detection
      },
      {
        label: 'Fair Price',
        data: fairPrices,
        borderColor: 'rgba(54, 162, 235, 1)',
        fill: '-1', // Fill between this dataset and the one above
        pointRadius: 0,
        pointHitRadius: 10, // Increase hit radius for better hover detection
      },
      {
        label: 'Support Price',
        data: supportPrices,
        borderColor: 'rgba(75, 192, 192, 1)',
        fill: false,
        pointRadius: 0,
        pointHitRadius: 10, // Increase hit radius for better hover detection
      },
    ],
  };

  const options = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'year',
          tooltipFormat: 'yyyy-MM-dd',
          displayFormats: {
            year: 'yyyy-MM-dd',
          }
        },
        title: {
          display: true,
          text: 'Date',
        },
        min: dates[0].getTime(),
        max: dates[dates.length - 1].getTime(),
        ticks: {
          callback: function (value, index, values) {
            const date = new Date(value);
            return ' ' + date.toISOString().split('T')[0]; // Add space before date to move it left
          },
        },
      },
      y: {
        type: 'logarithmic',
        title: {
          display: true,
          text: 'Price',
        },
        ticks: {
          callback: function (value) {
            const logValue = Math.log10(value);
            if (Number.isInteger(logValue)) {
              return value;
            }
            return null;
          },
        },
      },
    },
    plugins: {
      annotation: {
        annotations: annotations
      },
      zoom: {
        zoom: {
          drag: {
            enabled: true,
            borderColor: 'rgba(54, 162, 235, 0.5)',
            borderWidth: 1,
            backgroundColor: 'rgba(54, 162, 235, 0.3)',
          },
          mode: 'xy',
        },
        pan: {
          enabled: false,
        },
      },
      tooltip: {
        enabled: false, // Disable the default tooltip
        external: function(context) {
          // Custom tooltip handler
          const { chart, tooltip } = context;
          const tooltipEl = document.getElementById('chartjs-tooltip');

          // Create element on first render
          if (!tooltipEl) {
            const tooltipEl = document.createElement('div');
            tooltipEl.id = 'chartjs-tooltip';
            tooltipEl.style.position = 'absolute';
            tooltipEl.style.backgroundColor = 'rgba(0, 0, 0, 0.7)';
            tooltipEl.style.color = 'white';
            tooltipEl.style.padding = '8px';
            tooltipEl.style.borderRadius = '4px';
            tooltipEl.style.pointerEvents = 'none';
            document.body.appendChild(tooltipEl);
          }

          // Hide if no tooltip
          if (tooltip.opacity === 0) {
            tooltipEl.style.opacity = 0;
            return;
          }

          // Set caret position
          tooltipEl.style.transform = `translate(${tooltip.caretX}px, ${tooltip.caretY}px)`;

          // Set text
          if (tooltip.body) {
            const titleLines = tooltip.title || [];
            const bodyLines = tooltip.body.map(b => b.lines);

            let innerHtml = '<thead>';

            titleLines.forEach(title => {
              innerHtml += `<tr><th>${title}</th></tr>`;
            });
            innerHtml += '</thead><tbody>';

            bodyLines.forEach((body, i) => {
              const colors = tooltip.labelColors[i];
              let style = `background:${colors.backgroundColor}`;
              style += `; border-color:${colors.borderColor}`;
              style += `; border-width: 2px`;
              const span = `<span style="${style}"></span>`;
              innerHtml += `<tr><td>${span}${body}</td></tr>`;
            });
            innerHtml += '</tbody>';

            tooltipEl.innerHTML = innerHtml;
          }

          tooltipEl.style.opacity = 1;
        },
      },
    },
  };

  return <Line ref={chartRef} data={data} options={options} />;
};

export default PriceChart;
