import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  LogarithmicScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
} from 'chart.js';
import annotationPlugin from 'chartjs-plugin-annotation';

// Register necessary components
ChartJS.register(
  CategoryScale,
  LinearScale,
  LogarithmicScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  Filler,
  annotationPlugin
);

const getResistancePrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.14005841) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};
const getFairPrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.25978043) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};
const getSupportPrice = (daysSinceGb) => {
  return parseFloat((Math.pow(10, -13.37950245) * Math.pow(daysSinceGb, 4.218461)).toFixed(7));
};

const PriceChart = () => {
  const days = Array.from({ length: 3650 - 100 + 1 }, (_, i) => i + 100); // Example for 100 to 3650 days
  const years = days.map(day => (day / 365).toFixed(2)); // Convert days to years with 2 decimal precision
  const resistancePrices = days.map(getResistancePrice);
  const fairPrices = days.map(getFairPrice);
  const supportPrices = days.map(getSupportPrice);

  const data = {
    labels: years,
    datasets: [
      {
        label: 'Resistance Price',
        data: resistancePrices,
        borderColor: 'rgba(255, 99, 132, 1)',
        fill: false,
        pointRadius: 0,
      },
      {
        label: 'Fair Price',
        data: fairPrices,
        borderColor: 'rgba(54, 162, 235, 1)',
        fill: '-1', // Fill between this dataset and the one above
        pointRadius: 0,
      },
      {
        label: 'Support Price',
        data: supportPrices,
        borderColor: 'rgba(75, 192, 192, 1)',
        fill: false,
        pointRadius: 0,
      },
    ],
  };

  const options = {
    scales: {
      x: {
        type: 'linear',
        title: {
          display: true,
          text: 'Years Since Gb',
        },
        min: 100 / 365,
        max: 3650 / 365,
        ticks: {
          stepSize: 1,
          callback: function (value) {
            return value.toFixed(0); // Display integer years
          },
          autoSkip: false,
        },
      },
      y: {
        type: 'logarithmic',
        title: {
          display: true,
          text: 'Price',
        },
        ticks: {
          callback: function (value) {
            const logValue = Math.log10(value);
            if (Number.isInteger(logValue)) {
              return value;
            }
            return null;
          },
        },
      },
    },
    plugins: {
      annotation: {
        annotations: [
          {
            type: 'box',
            yScaleID: 'y',
            yMin: Math.min(...supportPrices),
            yMax: Math.max(...fairPrices),
            backgroundColor: 'rgba(75, 192, 192, 0.1)',
          },
          {
            type: 'box',
            yScaleID: 'y',
            yMin: Math.max(...fairPrices),
            yMax: Math.max(...resistancePrices),
            backgroundColor: 'rgba(255, 99, 132, 0.1)',
          },
        ],
      },
    },
  };

  return <Line data={data} options={options} />;
};

export default PriceChart;
